{
  "actions": [
    {
      "title": "Analyze Images (Vision)",
      "description": "Analyzes the content of an image using a multimodal model to generate human-readable insights about given visual data.",
      "type": "action",
      "documentation": "https://console.groq.com/docs/api-reference#vision-analyze",
      "endpointUrl": "https://api.groq.com/v1/vision/analyze",
      "apiBaseUrl": "https://api.groq.com",
      "method": "POST",
      "sampleResponse": {
        "insights": "A human readable description of the image",
        "details": {
          "objects": ["object1", "object2"],
          "colors": ["red", "blue"]
        }
      },
      "inputSchema": {
        "type": "object",
        "description": "Input parameters for analyzing an image.",
        "properties": {
          "imageUrl": {
            "type": "string",
            "description": "Public URL of the image to analyze."
          },
          "options": {
            "type": "object",
            "description": "Optional settings for image analysis.",
            "properties": {
              "detectionThreshold": {
                "type": "number",
                "description": "Threshold value to filter detected items."
              }
            }
          }
        },
        "required": ["imageUrl"]
      },
      "outputType": {
        "outputType": "object"
      }
    },
    {
      "title": "Create Chat Completion",
      "description": "Creates a model response for the given chat conversation.",
      "type": "action",
      "documentation": "https://console.groq.com/docs/api-reference#chat-completion",
      "endpointUrl": "https://api.groq.com/v1/chat/completion",
      "apiBaseUrl": "https://api.groq.com",
      "method": "POST",
      "sampleResponse": {
        "id": "chatcmpl-123",
        "response": "This is the generated response based on the conversation."
      },
      "inputSchema": {
        "type": "object",
        "description": "Input parameters for creating a chat completion.",
        "properties": {
          "messages": {
            "type": "array",
            "description": "An array of message objects representing the conversation.",
            "items": {
              "type": "object",
              "description": "A single chat message with role and content.",
              "properties": {
                "role": {
                  "type": "string",
                  "description": "Role of the message sender (e.g., user, system, assistant)."
                },
                "content": {
                  "type": "string",
                  "description": "Text content of the message."
                }
              },
              "required": ["role", "content"]
            }
          },
          "parameters": {
            "type": "object",
            "description": "Additional parameters for chat completion (e.g., temperature, max_tokens).",
            "properties": {
              "temperature": {
                "type": "number",
                "description": "Sampling temperature to use for completion."
              },
              "max_tokens": {
                "type": "number",
                "description": "Maximum number of tokens to generate."
              }
            }
          }
        },
        "required": ["messages"]
      },
      "outputType": {
        "outputType": "object"
      }
    },
    {
      "title": "Create JSON Chat Completion",
      "description": "Creates a model response for a given chat conversation with the response formatted as JSON.",
      "type": "action",
      "documentation": "https://console.groq.com/docs/api-reference#json-chat-completion",
      "endpointUrl": "https://api.groq.com/v1/chat/completion/json",
      "apiBaseUrl": "https://api.groq.com",
      "method": "POST",
      "sampleResponse": {
        "id": "chatcmpl-456",
        "response": {
          "message": "Generated response in JSON format.",
          "metadata": {
            "usage": "token usage details"
          }
        }
      },
      "inputSchema": {
        "type": "object",
        "description": "Input parameters for creating a JSON chat completion.",
        "properties": {
          "messages": {
            "type": "array",
            "description": "An array of chat message objects.",
            "items": {
              "type": "object",
              "description": "A single chat message.",
              "properties": {
                "role": {
                  "type": "string",
                  "description": "Role of the sender (e.g., user, assistant)."
                },
                "content": {
                  "type": "string",
                  "description": "Content of the message."
                }
              },
              "required": ["role", "content"]
            }
          },
          "parameters": {
            "type": "object",
            "description": "Additional options for the chat completion.",
            "properties": {
              "temperature": {
                "type": "number",
                "description": "Sampling temperature setting."
              },
              "max_tokens": {
                "type": "number",
                "description": "Maximum tokens to generate."
              }
            }
          }
        },
        "required": ["messages"]
      },
      "outputType": {
        "outputType": "object"
      }
    },
    {
      "title": "Create Transcription (Whisper)",
      "description": "Generates a transcription of audio content to text.",
      "type": "action",
      "documentation": "https://console.groq.com/docs/api-reference#transcription",
      "endpointUrl": "https://api.groq.com/v1/audio/transcription",
      "apiBaseUrl": "https://api.groq.com",
      "method": "POST",
      "sampleResponse": {
        "transcription": "This is the transcribed text from the audio input."
      },
      "inputSchema": {
        "type": "object",
        "description": "Input parameters for generating a transcription.",
        "properties": {
          "audioUrl": {
            "type": "string",
            "description": "URL of the audio file to transcribe."
          },
          "language": {
            "type": "string",
            "description": "Language code of the audio (optional)."
          }
        },
        "required": ["audioUrl"]
      },
      "outputType": {
        "outputType": "object"
      }
    },
    {
      "title": "Create Translation (Whisper)",
      "description": "Translates spoken words in an audio or video file to English.",
      "type": "action",
      "documentation": "https://console.groq.com/docs/api-reference#translation",
      "endpointUrl": "https://api.groq.com/v1/audio/translation",
      "apiBaseUrl": "https://api.groq.com",
      "method": "POST",
      "sampleResponse": {
        "translation": "This is the translated text in English."
      },
      "inputSchema": {
        "type": "object",
        "description": "Input parameters for translating audio content.",
        "properties": {
          "mediaUrl": {
            "type": "string",
            "description": "URL of the audio or video file to translate."
          },
          "sourceLanguage": {
            "type": "string",
            "description": "Language code of the original media (optional)."
          }
        },
        "required": ["mediaUrl"]
      },
      "outputType": {
        "outputType": "object"
      }
    },
    {
      "title": "Make an API Call",
      "description": "Performs an arbitrary authorized API call.",
      "type": "action",
      "documentation": "https://console.groq.com/docs/api-reference#api-call",
      "endpointUrl": "https://api.groq.com/v1/api/call",
      "apiBaseUrl": "https://api.groq.com",
      "method": "POST",
      "sampleResponse": {
        "status": "success",
        "data": {}
      },
      "inputSchema": {
        "type": "object",
        "description": "Input parameters for making a custom API call.",
        "properties": {
          "method": {
            "type": "string",
            "description": "HTTP method for the API call (e.g., GET, POST, PUT, DELETE)."
          },
          "url": {
            "type": "string",
            "description": "The URL path to the target endpoint."
          },
          "headers": {
            "type": "object",
            "description": "Custom headers to include in the API call."
          },
          "body": {
            "type": "object",
            "description": "Request payload for methods that require a body."
          }
        },
        "required": ["method", "url"]
      },
      "outputType": {
        "outputType": "object"
      }
    },
    {
      "title": "Find Models",
      "description": "Retrieves a list of available models.",
      "type": "action",
      "documentation": "https://console.groq.com/docs/api-reference#models",
      "endpointUrl": "https://api.groq.com/v1/models",
      "apiBaseUrl": "https://api.groq.com",
      "method": "GET",
      "sampleResponse": {
        "models": [
          {
            "id": "model-1",
            "name": "Model One"
          },
          {
            "id": "model-2",
            "name": "Model Two"
          }
        ]
      },
      "inputSchema": {
        "type": "object",
        "description": "Input parameters for finding models.",
        "properties": {
          "search": {
            "type": "string",
            "description": "Search term to filter models."
          }
        }
      },
      "outputType": {
        "outputType": "object",
        "arrayPropertyLabel": "models",
        "arrayPropertyValue": "models"
      }
    }
  ],
  "authentication": {
    "type": "oauth2",
    "authUrl": "https://api.groq.com/oauth/authorize",
    "tokenUrl": "https://api.groq.com/oauth/token",
    "profileInfoUrl": "https://api.groq.com/me"
  },
  "serviceJson": {
    "name": "appmixer.groq",
    "label": "Groq",
    "category": "applications",
    "description": "Integration with Groq's API for multimodal content analysis, chat completions, audio transcription, translation, and more.",
    "version": "1.0.0",
    "icon": "https://groq.com/assets/icon.svg"
  }
}
