{
  "actions": [
    {
      "title": "Create a Board",
      "description": "Creates a new board on Pinterest.",
      "type": "action",
      "documentation": "https://developers.pinterest.com/docs/api/v5/boards/#create-board",
      "endpointUrl": "https://api.pinterest.com/v5/boards",
      "apiBaseUrl": "https://api.pinterest.com/v5",
      "method": "POST",
      "sampleResponse": {
        "id": "123456789",
        "name": "My New Board",
        "description": "A board created via API."
      },
      "inputSchema": {
        "type": "object",
        "properties": {
          "name": {
            "type": "string",
            "description": "The name of the board."
          },
          "description": {
            "type": "string",
            "description": "A description for the board."
          }
        },
        "required": [
          "name"
        ]
      },
      "outputType": {
        "outputType": "object"
      }
    },
    {
      "title": "Create a Pin",
      "description": "Creates a new pin on a specified board.",
      "type": "action",
      "documentation": "https://developers.pinterest.com/docs/api/v5/pins/#create-pin",
      "endpointUrl": "https://api.pinterest.com/v5/pins",
      "apiBaseUrl": "https://api.pinterest.com/v5",
      "method": "POST",
      "sampleResponse": {
        "id": "987654321",
        "title": "New Pin",
        "board_id": "123456789"
      },
      "inputSchema": {
        "type": "object",
        "properties": {
          "board_id": {
            "type": "string",
            "description": "The ID of the board where the pin will be created."
          },
          "title": {
            "type": "string",
            "description": "The title of the pin."
          },
          "description": {
            "type": "string",
            "description": "A description for the pin."
          },
          "image_url": {
            "type": "string",
            "description": "The URL of the image for the pin."
          }
        },
        "required": [
          "board_id",
          "title",
          "image_url"
        ]
      },
      "outputType": {
        "outputType": "object"
      }
    },
    {
      "title": "Delete a Board",
      "description": "Deletes a specific board.",
      "type": "action",
      "documentation": "https://developers.pinterest.com/docs/api/v5/boards/#delete-board",
      "endpointUrl": "https://api.pinterest.com/v5/boards/{board_id}",
      "apiBaseUrl": "https://api.pinterest.com/v5",
      "method": "DELETE",
      "sampleResponse": {
        "status": "success",
        "message": "Board deleted successfully."
      },
      "inputSchema": {
        "type": "object",
        "properties": {
          "board_id": {
            "type": "string",
            "description": "The ID of the board to delete."
          }
        },
        "required": [
          "board_id"
        ]
      },
      "outputType": {
        "outputType": "object"
      }
    },
    {
      "title": "Delete a Pin",
      "description": "Deletes a selected pin.",
      "type": "action",
      "documentation": "https://developers.pinterest.com/docs/api/v5/pins/#delete-pin",
      "endpointUrl": "https://api.pinterest.com/v5/pins/{pin_id}",
      "apiBaseUrl": "https://api.pinterest.com/v5",
      "method": "DELETE",
      "sampleResponse": {
        "status": "success",
        "message": "Pin deleted successfully."
      },
      "inputSchema": {
        "type": "object",
        "properties": {
          "pin_id": {
            "type": "string",
            "description": "The ID of the pin to delete."
          }
        },
        "required": [
          "pin_id"
        ]
      },
      "outputType": {
        "outputType": "object"
      }
    },
    {
      "title": "Get a Board",
      "description": "Retrieves information about a specific board.",
      "type": "action",
      "documentation": "https://developers.pinterest.com/docs/api/v5/boards/#get-board",
      "endpointUrl": "https://api.pinterest.com/v5/boards/{board_id}",
      "apiBaseUrl": "https://api.pinterest.com/v5",
      "method": "GET",
      "sampleResponse": {
        "id": "123456789",
        "name": "My Board",
        "description": "Board description here."
      },
      "inputSchema": {
        "type": "object",
        "properties": {
          "board_id": {
            "type": "string",
            "description": "The ID of the board to retrieve."
          }
        },
        "required": [
          "board_id"
        ]
      },
      "outputType": {
        "outputType": "object"
      }
    },
    {
      "title": "Get a Pin",
      "description": "Returns information about a specific pin.",
      "type": "action",
      "documentation": "https://developers.pinterest.com/docs/api/v5/pins/#get-pin",
      "endpointUrl": "https://api.pinterest.com/v5/pins/{pin_id}",
      "apiBaseUrl": "https://api.pinterest.com/v5",
      "method": "GET",
      "sampleResponse": {
        "id": "987654321",
        "title": "Pin Title",
        "description": "Pin description.",
        "board_id": "123456789"
      },
      "inputSchema": {
        "type": "object",
        "properties": {
          "pin_id": {
            "type": "string",
            "description": "The ID of the pin to retrieve."
          }
        },
        "required": [
          "pin_id"
        ]
      },
      "outputType": {
        "outputType": "object"
      }
    },
    {
      "title": "Make an API Call",
      "description": "Performs an arbitrary authorized API call to Pinterest. This action is useful for executing operations that are not covered by the other predefined actions.",
      "type": "action",
      "documentation": "https://developers.pinterest.com/docs/api/v5/introduction/",
      "endpointUrl": "https://api.pinterest.com/v5/{endpoint}",
      "apiBaseUrl": "https://api.pinterest.com/v5",
      "method": "POST",
      "sampleResponse": {
        "status": "success",
        "data": {}
      },
      "inputSchema": {
        "type": "object",
        "properties": {
          "endpoint": {
            "type": "string",
            "description": "The relative endpoint path (excluding the base URL) to call. For example: 'boards', 'pins/{pin_id}', etc."
          },
          "http_method": {
            "type": "string",
            "description": "The HTTP method to use for the call (GET, POST, PUT, PATCH, DELETE)."
          },
          "payload": {
            "type": "object",
            "description": "An optional JSON payload for methods that support bodies (e.g., POST, PUT, PATCH)."
          }
        },
        "required": [
          "endpoint",
          "http_method"
        ]
      },
      "outputType": {
        "outputType": "object"
      }
    },
    {
      "title": "Update a Board",
      "description": "Updates an existing board with new information.",
      "type": "action",
      "documentation": "https://developers.pinterest.com/docs/api/v5/boards/#update-board",
      "endpointUrl": "https://api.pinterest.com/v5/boards/{board_id}",
      "apiBaseUrl": "https://api.pinterest.com/v5",
      "method": "PATCH",
      "sampleResponse": {
        "id": "123456789",
        "name": "Updated Board Name",
        "description": "Updated description."
      },
      "inputSchema": {
        "type": "object",
        "properties": {
          "board_id": {
            "type": "string",
            "description": "The ID of the board to update."
          },
          "name": {
            "type": "string",
            "description": "The new name for the board."
          },
          "description": {
            "type": "string",
            "description": "The new description for the board."
          }
        },
        "required": [
          "board_id"
        ]
      },
      "outputType": {
        "outputType": "object"
      }
    }
  ],
  "authentication": {
    "type": "oauth2",
    "authUrl": "https://www.pinterest.com/oauth/",
    "tokenUrl": "https://api.pinterest.com/v5/oauth/token",
    "profileInfoUrl": "https://api.pinterest.com/v5/user_account"
  },
  "serviceJson": {
    "name": "appmixer.pinterest",
    "label": "Pinterest",
    "category": "applications",
    "description": "Connector for interacting with Pinterest via its API. Use this connector to create boards, pins and perform various Pinterest operations.",
    "version": "1.0.0",
    "icon": "https://upload.wikimedia.org/wikipedia/commons/3/35/Pinterest_Logo.png"
  }
}
