{
  "actions": [
    {
      "title": "Create Payment Intent",
      "description": "Creates a Payment Intent to initiate a payment process with specified parameters like amount and currency.",
      "type": "action",
      "documentation": "https://stripe.com/docs/api/payment_intents/create",
      "endpointUrl": "https://api.stripe.com/v1/payment_intents",
      "apiBaseUrl": "https://api.stripe.com",
      "method": "POST",
      "sampleResponse": {
        "id": "pi_1Example",
        "object": "payment_intent",
        "amount": 1000,
        "currency": "usd",
        "status": "requires_confirmation"
      },
      "inputSchema": {
        "type": "object",
        "description": "Input parameters for creating a Payment Intent",
        "properties": {
          "amount": {
            "type": "number",
            "description": "Amount intended to be collected by this Payment Intent, measured in the smallest currency unit."
          },
          "currency": {
            "type": "string",
            "description": "Three-letter ISO currency code, in lowercase."
          },
          "payment_method": {
            "type": "string",
            "description": "The payment method ID to attach to this Payment Intent (optional)."
          },
          "description": {
            "type": "string",
            "description": "An arbitrary string to attach to the Payment Intent for additional information (optional)."
          }
        },
        "required": [
          "amount",
          "currency"
        ]
      },
      "outputType": {
        "outputType": "object",
        "arrayPropertyLabel": "",
        "arrayPropertyValue": ""
      }
    },
    {
      "title": "Confirm Payment Intent",
      "description": "Confirms an existing Payment Intent by its ID, initiating the payment process.",
      "type": "action",
      "documentation": "https://stripe.com/docs/api/payment_intents/confirm",
      "endpointUrl": "https://api.stripe.com/v1/payment_intents/{payment_intent_id}/confirm",
      "apiBaseUrl": "https://api.stripe.com",
      "method": "POST",
      "sampleResponse": {
        "id": "pi_1Example",
        "object": "payment_intent",
        "status": "requires_action"
      },
      "inputSchema": {
        "type": "object",
        "description": "Input parameters for confirming a Payment Intent",
        "properties": {
          "payment_intent_id": {
            "type": "string",
            "description": "The identifier of the Payment Intent to confirm."
          },
          "payment_method": {
            "type": "string",
            "description": "Optional payment method to attach during confirmation."
          }
        },
        "required": [
          "payment_intent_id"
        ]
      },
      "outputType": {
        "outputType": "object",
        "arrayPropertyLabel": "",
        "arrayPropertyValue": ""
      }
    },
    {
      "title": "Capture Payment Intent",
      "description": "Captures an existing, uncaptured Payment Intent to complete a payment which was previously authorized.",
      "type": "action",
      "documentation": "https://stripe.com/docs/api/payment_intents/capture",
      "endpointUrl": "https://api.stripe.com/v1/payment_intents/{payment_intent_id}/capture",
      "apiBaseUrl": "https://api.stripe.com",
      "method": "POST",
      "sampleResponse": {
        "id": "pi_1Example",
        "object": "payment_intent",
        "status": "succeeded"
      },
      "inputSchema": {
        "type": "object",
        "description": "Input parameters for capturing a Payment Intent",
        "properties": {
          "payment_intent_id": {
            "type": "string",
            "description": "The identifier of the Payment Intent to capture."
          },
          "amount_to_capture": {
            "type": "number",
            "description": "The amount to capture, if different from the original amount (optional)."
          }
        },
        "required": [
          "payment_intent_id"
        ]
      },
      "outputType": {
        "outputType": "object",
        "arrayPropertyLabel": "",
        "arrayPropertyValue": ""
      }
    },
    {
      "title": "Retrieve Payment Intent",
      "description": "Retrieves the details of a previously created Payment Intent.",
      "type": "action",
      "documentation": "https://stripe.com/docs/api/payment_intents/retrieve",
      "endpointUrl": "https://api.stripe.com/v1/payment_intents/{payment_intent_id}",
      "apiBaseUrl": "https://api.stripe.com",
      "method": "GET",
      "sampleResponse": {
        "id": "pi_1Example",
        "object": "payment_intent",
        "amount": 1000,
        "currency": "usd",
        "status": "requires_confirmation"
      },
      "inputSchema": {
        "type": "object",
        "description": "Input parameters for retrieving a Payment Intent",
        "properties": {
          "payment_intent_id": {
            "type": "string",
            "description": "The identifier of the Payment Intent to retrieve."
          }
        },
        "required": [
          "payment_intent_id"
        ]
      },
      "outputType": {
        "outputType": "object",
        "arrayPropertyLabel": "",
        "arrayPropertyValue": ""
      }
    },
    {
      "title": "Cancel Payment Intent",
      "description": "Cancels a Payment Intent by its ID. For payment intents that require capture, the remaining capturable amount will be automatically refunded.",
      "type": "action",
      "documentation": "https://stripe.com/docs/api/payment_intents/cancel",
      "endpointUrl": "https://api.stripe.com/v1/payment_intents/{payment_intent_id}/cancel",
      "apiBaseUrl": "https://api.stripe.com",
      "method": "POST",
      "sampleResponse": {
        "id": "pi_1Example",
        "object": "payment_intent",
        "status": "canceled"
      },
      "inputSchema": {
        "type": "object",
        "description": "Input parameters for canceling a Payment Intent",
        "properties": {
          "payment_intent_id": {
            "type": "string",
            "description": "The identifier of the Payment Intent to cancel."
          }
        },
        "required": [
          "payment_intent_id"
        ]
      },
      "outputType": {
        "outputType": "object",
        "arrayPropertyLabel": "",
        "arrayPropertyValue": ""
      }
    },
    {
      "title": "Create Customer",
      "description": "Creates a new customer object to store and manage customer information.",
      "type": "action",
      "documentation": "https://stripe.com/docs/api/customers/create",
      "endpointUrl": "https://api.stripe.com/v1/customers",
      "apiBaseUrl": "https://api.stripe.com",
      "method": "POST",
      "sampleResponse": {
        "id": "cus_1Example",
        "object": "customer",
        "email": "customer@example.com"
      },
      "inputSchema": {
        "type": "object",
        "description": "Input parameters for creating a Customer",
        "properties": {
          "email": {
            "type": "string",
            "description": "Customer's email address."
          },
          "description": {
            "type": "string",
            "description": "An arbitrary string that you can attach to a customer for additional information (optional)."
          },
          "payment_method": {
            "type": "string",
            "description": "A payment method ID to attach to the customer (optional)."
          }
        },
        "required": [
          "email"
        ]
      },
      "outputType": {
        "outputType": "object",
        "arrayPropertyLabel": "",
        "arrayPropertyValue": ""
      }
    },
    {
      "title": "Retrieve Customer",
      "description": "Retrieves the details of an existing customer by their ID.",
      "type": "action",
      "documentation": "https://stripe.com/docs/api/customers/retrieve",
      "endpointUrl": "https://api.stripe.com/v1/customers/{customer_id}",
      "apiBaseUrl": "https://api.stripe.com",
      "method": "GET",
      "sampleResponse": {
        "id": "cus_1Example",
        "object": "customer",
        "email": "customer@example.com"
      },
      "inputSchema": {
        "type": "object",
        "description": "Input parameters for retrieving a Customer",
        "properties": {
          "customer_id": {
            "type": "string",
            "description": "The identifier of the Customer to retrieve."
          }
        },
        "required": [
          "customer_id"
        ]
      },
      "outputType": {
        "outputType": "object",
        "arrayPropertyLabel": "",
        "arrayPropertyValue": ""
      }
    },
    {
      "title": "Update Customer",
      "description": "Updates specified properties of an existing customer.",
      "type": "action",
      "documentation": "https://stripe.com/docs/api/customers/update",
      "endpointUrl": "https://api.stripe.com/v1/customers/{customer_id}",
      "apiBaseUrl": "https://api.stripe.com",
      "method": "POST",
      "sampleResponse": {
        "id": "cus_1Example",
        "object": "customer",
        "email": "newemail@example.com"
      },
      "inputSchema": {
        "type": "object",
        "description": "Input parameters for updating a Customer",
        "properties": {
          "customer_id": {
            "type": "string",
            "description": "The identifier of the Customer to update."
          },
          "email": {
            "type": "string",
            "description": "New email address for the customer (optional)."
          },
          "description": {
            "type": "string",
            "description": "New description for the customer (optional)."
          }
        },
        "required": [
          "customer_id"
        ]
      },
      "outputType": {
        "outputType": "object",
        "arrayPropertyLabel": "",
        "arrayPropertyValue": ""
      }
    },
    {
      "title": "Delete Customer",
      "description": "Permanently deletes a customer. This operation is irreversible.",
      "type": "action",
      "documentation": "https://stripe.com/docs/api/customers/delete",
      "endpointUrl": "https://api.stripe.com/v1/customers/{customer_id}",
      "apiBaseUrl": "https://api.stripe.com",
      "method": "DELETE",
      "sampleResponse": {
        "id": "cus_1Example",
        "object": "customer",
        "deleted": true
      },
      "inputSchema": {
        "type": "object",
        "description": "Input parameter for deleting a Customer",
        "properties": {
          "customer_id": {
            "type": "string",
            "description": "The identifier of the Customer to delete."
          }
        },
        "required": [
          "customer_id"
        ]
      },
      "outputType": {
        "outputType": "object",
        "arrayPropertyLabel": "",
        "arrayPropertyValue": ""
      }
    },
    {
      "title": "Create Refund",
      "description": "Creates a refund for a specified charge, returning funds to the customer.",
      "type": "action",
      "documentation": "https://stripe.com/docs/api/refunds/create",
      "endpointUrl": "https://api.stripe.com/v1/refunds",
      "apiBaseUrl": "https://api.stripe.com",
      "method": "POST",
      "sampleResponse": {
        "id": "re_1Example",
        "object": "refund",
        "amount": 1000,
        "status": "succeeded"
      },
      "inputSchema": {
        "type": "object",
        "description": "Input parameters for creating a Refund",
        "properties": {
          "charge": {
            "type": "string",
            "description": "The identifier of the charge to refund."
          },
          "amount": {
            "type": "number",
            "description": "The amount to refund, if partial refund is desired (optional)."
          },
          "reason": {
            "type": "string",
            "description": "Reason for the refund (optional)."
          }
        },
        "required": [
          "charge"
        ]
      },
      "outputType": {
        "outputType": "object",
        "arrayPropertyLabel": "",
        "arrayPropertyValue": ""
      }
    },
    {
      "title": "Retrieve Refund",
      "description": "Retrieves details about an existing refund using its ID.",
      "type": "action",
      "documentation": "https://stripe.com/docs/api/refunds/retrieve",
      "endpointUrl": "https://api.stripe.com/v1/refunds/{refund_id}",
      "apiBaseUrl": "https://api.stripe.com",
      "method": "GET",
      "sampleResponse": {
        "id": "re_1Example",
        "object": "refund",
        "amount": 1000,
        "status": "succeeded"
      },
      "inputSchema": {
        "type": "object",
        "description": "Input parameter for retrieving a Refund",
        "properties": {
          "refund_id": {
            "type": "string",
            "description": "The identifier of the Refund to retrieve."
          }
        },
        "required": [
          "refund_id"
        ]
      },
      "outputType": {
        "outputType": "object",
        "arrayPropertyLabel": "",
        "arrayPropertyValue": ""
      }
    },
    {
      "title": "Create Invoice",
      "description": "Creates a new invoice for a customer, which can later be finalized and paid.",
      "type": "action",
      "documentation": "https://stripe.com/docs/api/invoices/create",
      "endpointUrl": "https://api.stripe.com/v1/invoices",
      "apiBaseUrl": "https://api.stripe.com",
      "method": "POST",
      "sampleResponse": {
        "id": "in_1Example",
        "object": "invoice",
        "customer": "cus_1Example",
        "status": "draft"
      },
      "inputSchema": {
        "type": "object",
        "description": "Input parameters for creating an Invoice",
        "properties": {
          "customer": {
            "type": "string",
            "description": "The identifier of the customer to invoice."
          },
          "collection_method": {
            "type": "string",
            "description": "Either 'charge_automatically' or 'send_invoice' (optional)."
          },
          "description": {
            "type": "string",
            "description": "An optional description for the invoice."
          }
        },
        "required": [
          "customer"
        ]
      },
      "outputType": {
        "outputType": "object",
        "arrayPropertyLabel": "",
        "arrayPropertyValue": ""
      }
    },
    {
      "title": "Finalize Draft Invoice",
      "description": "Finalizes a draft invoice, making it ready for payment.",
      "type": "action",
      "documentation": "https://stripe.com/docs/api/invoices/finalize",
      "endpointUrl": "https://api.stripe.com/v1/invoices/{invoice_id}/finalize",
      "apiBaseUrl": "https://api.stripe.com",
      "method": "POST",
      "sampleResponse": {
        "id": "in_1Example",
        "object": "invoice",
        "status": "open"
      },
      "inputSchema": {
        "type": "object",
        "description": "Input parameter for finalizing a Draft Invoice",
        "properties": {
          "invoice_id": {
            "type": "string",
            "description": "The identifier of the draft invoice to finalize."
          }
        },
        "required": [
          "invoice_id"
        ]
      },
      "outputType": {
        "outputType": "object",
        "arrayPropertyLabel": "",
        "arrayPropertyValue": ""
      }
    },
    {
      "title": "Retrieve Invoice",
      "description": "Retrieves the details of an existing invoice by its ID.",
      "type": "action",
      "documentation": "https://stripe.com/docs/api/invoices/retrieve",
      "endpointUrl": "https://api.stripe.com/v1/invoices/{invoice_id}",
      "apiBaseUrl": "https://api.stripe.com",
      "method": "GET",
      "sampleResponse": {
        "id": "in_1Example",
        "object": "invoice",
        "customer": "cus_1Example",
        "status": "open"
      },
      "inputSchema": {
        "type": "object",
        "description": "Input parameter for retrieving an Invoice",
        "properties": {
          "invoice_id": {
            "type": "string",
            "description": "The identifier of the Invoice to retrieve."
          }
        },
        "required": [
          "invoice_id"
        ]
      },
      "outputType": {
        "outputType": "object",
        "arrayPropertyLabel": "",
        "arrayPropertyValue": ""
      }
    },
    {
      "title": "Retrieve Balance",
      "description": "Retrieves the current account balance, including pending and available amounts.",
      "type": "action",
      "documentation": "https://stripe.com/docs/api/balance/retrieve",
      "endpointUrl": "https://api.stripe.com/v1/balance",
      "apiBaseUrl": "https://api.stripe.com",
      "method": "GET",
      "sampleResponse": {
        "object": "balance",
        "available": [
          {
            "amount": 1000,
            "currency": "usd"
          }
        ],
        "pending": []
      },
      "inputSchema": {
        "type": "object",
        "description": "No additional input parameters are required for retrieving balance."
      },
      "outputType": {
        "outputType": "object",
        "arrayPropertyLabel": "available",
        "arrayPropertyValue": "available"
      }
    },
    {
      "title": "Make an API Call",
      "description": "Performs an arbitrary authorized API call to any Stripe endpoint with custom parameters.",
      "type": "action",
      "documentation": "https://stripe.com/docs/api",
      "endpointUrl": "https://api.stripe.com/v1/{custom_endpoint}",
      "apiBaseUrl": "https://api.stripe.com",
      "method": "POST",
      "sampleResponse": {
        "message": "API call executed successfully",
        "data": {}
      },
      "inputSchema": {
        "type": "object",
        "description": "Input parameters for making a custom API call",
        "properties": {
          "custom_endpoint": {
            "type": "string",
            "description": "The custom endpoint path (e.g., 'charges')."
          },
          "method": {
            "type": "string",
            "description": "HTTP method to use for the API call (GET, POST, etc.)."
          },
          "parameters": {
            "type": "object",
            "description": "Key-value object containing parameters for the API call."
          }
        },
        "required": [
          "custom_endpoint",
          "method"
        ]
      },
      "outputType": {
        "outputType": "object",
        "arrayPropertyLabel": "",
        "arrayPropertyValue": ""
      }
    }
  ],
  "authentication": {
    "type": "oauth2",
    "authUrl": "https://connect.stripe.com/oauth/authorize",
    "tokenUrl": "https://connect.stripe.com/oauth/token",
    "profileInfoUrl": "https://api.stripe.com/v1/account"
  },
  "serviceJson": {
    "name": "appmixer.stripe",
    "label": "Stripe",
    "category": "applications",
    "description": "Stripe API integration for managing payments, customers, refunds, invoices, and balance operations.",
    "version": "1.0.0",
    "icon": "https://stripe.com/img/v3/home/twitter.png"
  }
}
