{
  "actions": [
    {
      "title": "Create a Task",
      "description": "Creates a new task in a specified task list.",
      "type": "action",
      "documentation": "https://developers.google.com/tasks/reference/rest/v1/tasks/insert",
      "endpointUrl": "/tasks/v1/lists/{tasklistId}/tasks",
      "apiBaseUrl": "https://www.googleapis.com",
      "method": "POST",
      "sampleResponse": {
        "id": "unique_task_id",
        "title": "Task Title",
        "notes": "Optional notes",
        "status": "needsAction",
        "due": "2023-12-31T23:59:59.000Z"
      },
      "inputSchema": {
        "type": "object",
        "description": "Input parameters for creating a task.",
        "properties": {
          "tasklistId": {
            "type": "string",
            "description": "The ID of the task list in which the task will be created."
          },
          "title": {
            "type": "string",
            "description": "The title of the task."
          },
          "notes": {
            "type": "string",
            "description": "Optional. Additional details about the task."
          },
          "due": {
            "type": "string",
            "description": "Optional. The due date of the task in RFC3339 timestamp format."
          }
        },
        "required": [
          "tasklistId",
          "title"
        ]
      },
      "outputType": {
        "outputType": "object"
      }
    },
    {
      "title": "Create a Task List",
      "description": "Creates a new task list for organizing tasks.",
      "type": "action",
      "documentation": "https://developers.google.com/tasks/reference/rest/v1/tasklists/insert",
      "endpointUrl": "/tasks/v1/users/@me/lists",
      "apiBaseUrl": "https://www.googleapis.com",
      "method": "POST",
      "sampleResponse": {
        "id": "unique_tasklist_id",
        "title": "My Task List"
      },
      "inputSchema": {
        "type": "object",
        "description": "Input parameters for creating a task list.",
        "properties": {
          "title": {
            "type": "string",
            "description": "The title of the task list."
          }
        },
        "required": [
          "title"
        ]
      },
      "outputType": {
        "outputType": "object"
      }
    },
    {
      "title": "Delete a Task",
      "description": "Deletes a specified task from a task list.",
      "type": "action",
      "documentation": "https://developers.google.com/tasks/reference/rest/v1/tasks/delete",
      "endpointUrl": "/tasks/v1/lists/{tasklistId}/tasks/{taskId}",
      "apiBaseUrl": "https://www.googleapis.com",
      "method": "DELETE",
      "sampleResponse": {},
      "inputSchema": {
        "type": "object",
        "description": "Input parameters for deleting a task.",
        "properties": {
          "tasklistId": {
            "type": "string",
            "description": "The ID of the task list containing the task."
          },
          "taskId": {
            "type": "string",
            "description": "The ID of the task to be deleted."
          }
        },
        "required": [
          "tasklistId",
          "taskId"
        ]
      },
      "outputType": {
        "outputType": "object"
      }
    },
    {
      "title": "Delete a Task List",
      "description": "Deletes a specified task list.",
      "type": "action",
      "documentation": "https://developers.google.com/tasks/reference/rest/v1/tasklists/delete",
      "endpointUrl": "/tasks/v1/tasklists/{tasklistId}",
      "apiBaseUrl": "https://www.googleapis.com",
      "method": "DELETE",
      "sampleResponse": {},
      "inputSchema": {
        "type": "object",
        "description": "Input parameters for deleting a task list.",
        "properties": {
          "tasklistId": {
            "type": "string",
            "description": "The ID of the task list to be deleted."
          }
        },
        "required": [
          "tasklistId"
        ]
      },
      "outputType": {
        "outputType": "object"
      }
    },
    {
      "title": "Get a Task",
      "description": "Retrieves the details of a specified task.",
      "type": "action",
      "documentation": "https://developers.google.com/tasks/reference/rest/v1/tasks/get",
      "endpointUrl": "/tasks/v1/lists/{tasklistId}/tasks/{taskId}",
      "apiBaseUrl": "https://www.googleapis.com",
      "method": "GET",
      "sampleResponse": {
        "id": "unique_task_id",
        "title": "Task Title",
        "notes": "Task notes",
        "status": "needsAction",
        "due": "2023-12-31T23:59:59.000Z"
      },
      "inputSchema": {
        "type": "object",
        "description": "Input parameters for retrieving a task.",
        "properties": {
          "tasklistId": {
            "type": "string",
            "description": "The ID of the task list containing the task."
          },
          "taskId": {
            "type": "string",
            "description": "The ID of the task to retrieve."
          }
        },
        "required": [
          "tasklistId",
          "taskId"
        ]
      },
      "outputType": {
        "outputType": "object"
      }
    },
    {
      "title": "Get a Task List",
      "description": "Retrieves the details of a specified task list.",
      "type": "action",
      "documentation": "https://developers.google.com/tasks/reference/rest/v1/tasklists/get",
      "endpointUrl": "/tasks/v1/tasklists/{tasklistId}",
      "apiBaseUrl": "https://www.googleapis.com",
      "method": "GET",
      "sampleResponse": {
        "id": "unique_tasklist_id",
        "title": "My Task List"
      },
      "inputSchema": {
        "type": "object",
        "description": "Input parameters for retrieving a task list.",
        "properties": {
          "tasklistId": {
            "type": "string",
            "description": "The ID of the task list to retrieve."
          }
        },
        "required": [
          "tasklistId"
        ]
      },
      "outputType": {
        "outputType": "object"
      }
    },
    {
      "title": "Make an API Call",
      "description": "Performs an arbitrary authorized Google Tasks API call.",
      "type": "action",
      "documentation": "https://developers.google.com/tasks/reference/rest",
      "endpointUrl": "",
      "apiBaseUrl": "https://www.googleapis.com",
      "method": "GET",
      "sampleResponse": {
        "result": "API call result data"
      },
      "inputSchema": {
        "type": "object",
        "description": "Input parameters for making an arbitrary API call.",
        "properties": {
          "method": {
            "type": "string",
            "description": "HTTP method to use (GET, POST, etc.)."
          },
          "endpoint": {
            "type": "string",
            "description": "The specific API endpoint path (excluding base URL)."
          },
          "queryParams": {
            "type": "object",
            "description": "Optional query parameters as key-value pairs."
          },
          "body": {
            "type": "object",
            "description": "Optional request body."
          }
        },
        "required": [
          "method",
          "endpoint"
        ]
      },
      "outputType": {
        "outputType": "object"
      }
    },
    {
      "title": "Update a Task",
      "description": "Updates an existing task in a task list.",
      "type": "action",
      "documentation": "https://developers.google.com/tasks/reference/rest/v1/tasks/patch",
      "endpointUrl": "/tasks/v1/lists/{tasklistId}/tasks/{taskId}",
      "apiBaseUrl": "https://www.googleapis.com",
      "method": "PATCH",
      "sampleResponse": {
        "id": "unique_task_id",
        "title": "Updated Task Title",
        "notes": "Updated notes",
        "status": "completed",
        "due": "2023-12-31T23:59:59.000Z"
      },
      "inputSchema": {
        "type": "object",
        "description": "Input parameters for updating a task.",
        "properties": {
          "tasklistId": {
            "type": "string",
            "description": "The ID of the task list containing the task."
          },
          "taskId": {
            "type": "string",
            "description": "The ID of the task to be updated."
          },
          "title": {
            "type": "string",
            "description": "Optional. The new title for the task."
          },
          "notes": {
            "type": "string",
            "description": "Optional. Updated notes for the task."
          },
          "status": {
            "type": "string",
            "description": "Optional. The status of the task (e.g. 'needsAction' or 'completed')."
          },
          "due": {
            "type": "string",
            "description": "Optional. The new due date in RFC3339 timestamp format."
          }
        },
        "required": [
          "tasklistId",
          "taskId"
        ]
      },
      "outputType": {
        "outputType": "object"
      }
    },
    {
      "title": "Update a Task List",
      "description": "Updates the properties of an existing task list.",
      "type": "action",
      "documentation": "https://developers.google.com/tasks/reference/rest/v1/tasklists/patch",
      "endpointUrl": "/tasks/v1/tasklists/{tasklistId}",
      "apiBaseUrl": "https://www.googleapis.com",
      "method": "PATCH",
      "sampleResponse": {
        "id": "unique_tasklist_id",
        "title": "Updated Task List Title"
      },
      "inputSchema": {
        "type": "object",
        "description": "Input parameters for updating a task list.",
        "properties": {
          "tasklistId": {
            "type": "string",
            "description": "The ID of the task list to be updated."
          },
          "title": {
            "type": "string",
            "description": "Optional. The new title for the task list."
          }
        },
        "required": [
          "tasklistId"
        ]
      },
      "outputType": {
        "outputType": "object"
      }
    },
    {
      "title": "Find Task Lists",
      "description": "Retrieves all task lists for the current user, optionally filtered using a search query.",
      "type": "action",
      "documentation": "https://developers.google.com/tasks/reference/rest/v1/tasklists/list",
      "endpointUrl": "/tasks/v1/users/@me/lists",
      "apiBaseUrl": "https://www.googleapis.com",
      "method": "GET",
      "sampleResponse": {
        "items": [
          {
            "id": "tasklist_id_1",
            "title": "Personal"
          },
          {
            "id": "tasklist_id_2",
            "title": "Work"
          }
        ]
      },
      "inputSchema": {
        "type": "object",
        "description": "Input parameters for finding task lists.",
        "properties": {
          "query": {
            "type": "string",
            "description": "Optional. A search query to filter task lists."
          }
        }
      },
      "outputType": {
        "outputType": "array",
        "arrayPropertyLabel": "Task Lists",
        "arrayPropertyValue": "items"
      }
    },
    {
      "title": "Find Tasks",
      "description": "Retrieves all tasks from a specified task list, optionally filtered using a search query.",
      "type": "action",
      "documentation": "https://developers.google.com/tasks/reference/rest/v1/tasks/list",
      "endpointUrl": "/tasks/v1/lists/{tasklistId}/tasks",
      "apiBaseUrl": "https://www.googleapis.com",
      "method": "GET",
      "sampleResponse": {
        "items": [
          {
            "id": "task_id_1",
            "title": "Buy groceries"
          },
          {
            "id": "task_id_2",
            "title": "Schedule meeting"
          }
        ]
      },
      "inputSchema": {
        "type": "object",
        "description": "Input parameters for finding tasks.",
        "properties": {
          "tasklistId": {
            "type": "string",
            "description": "The ID of the task list from which to retrieve tasks."
          },
          "query": {
            "type": "string",
            "description": "Optional. A search query to filter tasks."
          }
        },
        "required": [
          "tasklistId"
        ]
      },
      "outputType": {
        "outputType": "array",
        "arrayPropertyLabel": "Tasks",
        "arrayPropertyValue": "items"
      }
    }
  ],
  "authentication": {
    "type": "oauth2",
    "authUrl": "https://accounts.google.com/o/oauth2/auth",
    "tokenUrl": "https://oauth2.googleapis.com/token",
    "profileInfoUrl": "https://www.googleapis.com/oauth2/v3/userinfo"
  },
  "serviceJson": {
    "name": "appmixer.google.tasks",
    "label": "Google Tasks",
    "category": "applications",
    "description": "Integration for Google Tasks, offering endpoints to create, update, delete, and find tasks and task lists.",
    "version": "1.0.0",
    "icon": "https://www.gstatic.com/images/branding/product/1x/tasks_48dp.png"
  }
}
